import React from 'react';

function QuestionCard(props) {
  const { questions } = props;


  return questions.map(question => {
    const { text, answer } = question;
    return (
      <div key={Math.random()} >
        <p>{text}</p>
        <p>{answer.toString()}</p>
      </div>
    );
  });
}

export default QuestionCard
